import os from 'os';
import Development from 'app/config/envirments/Development';
import Production from 'app/config/envirments/Production';
import Default from 'app/config/envirments/Production';

export default class Config {
    env = null;
    static instance = null;

    /**
     *
     * @return {Config}
     */
    static getInstance() {
        if(Config.instance === null) {
           Config.instance = new Config();
        }
        return Config.instance;
    }

    /**
     *
     * @return {Default}
     */
    static env() {

        if(os.hostname().indexOf("DESKTOP-") > -1) {
            return new Development();
        } else {
            return new Production();
        }


    }
}