import ApplicationController from 'app/controller/ApplicationController';

export default class DefaultRoutes {
    init(router) {
        console.log('Init Routs: ','DefaultRoutes')
        let applicationController = new ApplicationController();
        router.get('/history', (ctx, next)=> {

            applicationController.history(ctx);
        });

        router.get('/startRecording', (ctx, next)=> {

            applicationController.history(ctx);
        });

        router.get('/last6by5', (ctx, next)=> {

            applicationController.last6by5(ctx);
        });

        router.get('/orders/create', (ctx, next)=> {
           applicationController.createOrder(ctx);
        });
    }
}