export default class StockPosition
{
    /**
     * In milliseconds
     */
    time;
    symbol;
    price;
    amount;
    expiredStockPeriod = 300;
    constructor(time,price,amount,symbol) {
        this.time = time;
        this.symbol = symbol;
        this.price = price;
        this.amount = amount;
        this.symbol = symbol;
    }

    isExpired()
    {
        return (new Date().getTime()-this.time)/1000 > this.expiredStockPeriod;
    }

    getExpriationTime()
    {
        return new Date(this.time+this.expiredStockPeriod);
    }

    total()
    {
        return this.price*this.amount;
    }






}